import React from "react";
import styled from "styled-components";

const OneBtn = styled.button`
background : ${props => props.primary ? "palevioletred" : "transparent"};
color: ${props => props.primary ? "white" : "palevioletred"}; ;
font-size: 1em;
margin: 1em;
padding: 0.25em 1em;
border: 2px solid palevioletred;
border-radius: 3px;
cursor: pointer;
`;

const TomatoBtn = styled(OneBtn)`
color: tomato;
border-color: tomato;
`;

const Input = styled.input`
border: 2px solid ${props=> props.color || "palevioletred"};
color: ${props=> props.color || "palevioletred"};
padding: 0.35rem;
background: transparent;
margin: 0.25rem;
&:focus{
    outline :none;
}
`;

function Button() {
  return (
    <div>
            <OneBtn primary>
                Primary
            </OneBtn>

            <OneBtn>
                Normal
            </OneBtn>

            <TomatoBtn as="a" href="/">
                Tomato Button
            </TomatoBtn>
            <br/>
            <Input placeholder="Enter Text" color="mediumpurple" type="text"></Input>
            <Input placeholder="Enter Text Again" color="" type="text"></Input>
    </div>
  );
}

export default Button;
